package main;

/*
 * 변수 초기화하기
 * */
public class Ex2 {

	public static void main(String[] args) {
		int x = 10; 
		// 변수 선언과 동시에 값을 저장
		// "초기화" 라는 표현
		// 선언식 대입연산자 값까지 넣기
		System.out.println(x); // 출력

		int y = x; 
		// x의 값을 y에 복사
		// 값 대신 변수를 입력할 수도 있음
		// "복사" 한다 라는 표현
		System.out.println(y);
		// TODO Auto-generated method stub

	}
// 컨트롤 + 쉬프트 + f 정렬 해줌
}
